INSERT INTO `modx_site_snippets` VALUES ('15','MinifyX','<strong>1.0</strong> Minify CSS and JS','0','9','0','include($modx->config[\'base_path\'].\"assets/snippets/MinifyX/MinifyX.core.php\");\n$CSSfile=isset($CSSfile) ? $CSSfile : \'\';\n$JSfile=isset($JSfile) ? $JSfile : \'\';\n$CSSdoc=isset($CSSdoc) ? (int)$CSSdoc : 0;\n$JSdoc=isset($JSdoc) ? (int)$JSdoc : 0;\n\n\n$MinifyX=new MinifyX($modx);\nif($CSSfile!=\'\' || (int)$CSSdoc>0){\n  $flagCSS=$MinifyX->minCSS($CSSfile,$CSSdoc);\n  $modx->logEvent(46,1,\"CSSFILE: \".$CSSfile.\"<br />CSSDOC: \".$CSSdoc,\"MinifyX new CSS\");\n}\n\nif($JSfile!=\'\' || (int)$JSdoc>0){\n  $flagJS=$MinifyX->minJS($JSfile,$JSdoc);\n  $modx->logEvent(46,1,\"JSFILE: \".$JSfile.\"<br />JSDOC: \".$JSdoc,\"MinifyX newJS\");\n}\n\nif(isset($API)){\n  $data=array(\'js\'=>\'\',\'css\'=>\'\');\n  if($flagJS){\n  	$data[\'js\']=\"/\".$MinifyX->outFolder.$MinifyX->jsFile;\n  }\n  if($flagCSS){\n  	$data[\'css\']=\"/\".$MinifyX->outFolder.$MinifyX->cssFile;\n  }	\n}else{\n  $data=\'\';\n  $modx->logEvent(46,2,\"slow MODE\",\"MinifyX\");\n	if($flagJS){\n  	$modx->regClientCSS(\"/\".$MinifyX->outFolder.$MinifyX->jsFile);\n  }\n  if($flagCSS){\n  	$modx->regClientCSS(\"/\".$MinifyX->outFolder.$MinifyX->cssFile);\n  }\n}\n\nreturn $data;\n?>\n','0','','');
INSERT INTO `modx_site_snippets` VALUES ('17','DynamicDescription','<strong>1.0</strong> DynamicDescription 1.0','0','11','0','/**\n * Copyright (C) 2007 James Ehly\n *\n * This program is free software; you can redistribute it and/or modify\n * it under the terms of the GNU General Public License as published by\n * the Free Software Foundation; either version 2 of the License, or\n * (at your option) any later version.\n *\n * This program is distributed in the hope that it will be useful,\n * but WITHOUT ANY WARRANTY; without even the implied warranty of\n * MERCHANTABILITY or FITNESS FOR A PARTICULAR PURPOSE.  See the\n * GNU General Public License for more details.\n *\n * You should have received a copy of the GNU General Public License\n * along with this program; if not, write to the Free Software\n * Foundation, Inc., 59 Temple Place, Suite 330, Boston, MA  02111-1307  USA\n *\n */\n\n/**\n * Installation:\n *\n * Just copy this code into a new Snippet and name it DynamicDescription\n * Nothin\' to it :)\n *\n */\n\n/**\n * Usage:\n *\n * <META NAME=\"description\" CONTENT=\"[[DynamicDescription]]\">\n *\n * The above example is a typical way to use this snippet. This creates\n * a dynamic description of the first 25 words of the document content.\n * The meta tag and snippet call should be placed in the <head> portion\n * of your template.\n *\n * [[DynamicDescription? &descriptionTV=`MetaDescription`]]\n *\n * The above example creates a dynamic description of the MetaDescription\n * template variable. If MetaDescription is empty it will default to the\n * document content.  This is useful for the meta description tag, where\n * most documents can draw from the document content for a dynamic\n * description. You can use the template variable for pages comprised of\n * snippets and chunks only (like a blog page that only calls the ditto\n * snippet)\n *\n * [[DynamicDescription? &descriptionTV=`MetaDescription` &id=`39` &maxWordCount=`30`]]\n *\n * The above example creates a dynamic description of the MetaDescription\n * template variable for the document with an id of 39. If that is\n * empty then it will default to the content of that document. This also\n * changes the maxWordCount to 30.\n *\n */\n\n/* Parameters */\n\n/**\n * &maxWordCount\n * integer (25)\n * Sets the maximum word count of the description, default is 25 words\n */\n$maxWordCount = (!empty($maxWordCount) && is_numeric($maxWordCount)) ? $maxWordCount : 25;\n\n/**\n * &id\n * integer ($modx->documentObject[\'id\'])\n * the id of the page you want a description for, defaults to the\n * current page.\n */\n$id = (is_numeric($id) && !empty($id)) ? $id : $modx->documentObject[\'id\'];\n\n/**\n * &descriptionTV\n * string (\'\')\n * The name of a template variable you\'ve set up for the description.\n * If you have a separate template variable set up for a description\n * you write in, set that with this parameter.  If the parameter ends\n * up being empty, it will default back to the content. If it is not\n * empty then it will display the value of the TV and stop processing.\n * In other words, it will print it just as you typed it.\n */\n$descriptionTvArray = (!empty($descriptionTV)) ? $modx->getTemplateVarOutput($descriptionTV,$id) : \'\';\n$descriptionTV = $descriptionTvArray[$descriptionTV];\n\n\n/* Snippet Code */\n\n/**\n * Function: getDynaDescription()\n * Returns:  A string of text ready to be placed in a meta description tag\n */\nif (!function_exists(getDynaDescription)) {\n  function getDynaDescription($text=\'\',$excerpt_length=25)\n  {\n    global $modx;\n    $text = str_replace(\']]>\', \']]&gt;\', $text);\n    /* remove line breaks */\n    $text = str_replace(\"\\n\",\' \',$text);\n    $text = str_replace(\"\\r\",\' \',$text);\n    /* remove entity chars */\n    $text = preg_replace(\'~&.+;~U\',\' \',$text);\n    /* remove special MODx tags - chunks, snippets, etc.\n     * If we don\'t do this they\'ll end up expanded in the description.\n     */\n    $text = $modx->stripTags($text);\n    $words = preg_split (\"/\\s+/\", $text,$excerpt_length+1);\n    if (count($words) > $excerpt_length) {\n      array_pop($words);\n      array_push($words, \'...\');\n      $text = implode(\' \', $words);\n    }\n    return trim(stripslashes($text));\n  }\n}\n\nif (!empty($descriptionTV)) {\n  // if the $descriptionTV is not empty then we just print it with\n  // no extra processing, it\'s up to you to manage your description\n  echo $descriptionTV;\n} else {\n  // for everyone else that is lazy, just grab it from the content, yeah!\n  $content = $modx->getDocument($id,\'content\');\n  $content = $content[\'content\'];\n  echo getDynaDescription($content,$maxWordCount);\n}\n?>','0','','');
INSERT INTO `modx_site_snippets` VALUES ('19','Seo4Evo','<strong>RC 3.2</strong> Seo4Evo RC 3.2 - Manage and return Meta Tags using modx Tvs','0','11','0','/**\n| --------------------------------------------------------------------------\n| Snippet Title:     Seo4Evo By Nicola (Banzai) (based on MetaTagsExtra by Soda)\n| Snippet Version:  RC 3.2\n|\n| Description:\n| Manage and return Meta Tags using modx Tvs from Seo4Evo Package\n|\n| Basic Snippet Parameters:\n|\n| KeywordsTv - custom keywords tv - Example: &KeywordsTv=`documentTags`\n| MetaDescriptionTv -  custom description tv - Example: &MetaDescriptionTv=`introtext`\n| all_page_keywords - chunk or comma separated list of Keywords displayed on all pages - Example: &all_page_keywords=`modx, snippets, plugins`\n| preTitle -  custom pre title - Example: &preTitle &preTitle=`[(site_name)] |`\n| postTitle -  custom post title - Example:  &postTitle=`| [(site_name)]`\n|\n| ******* Facebook Open Graph  Parameters: ******\n| OpenGraph - enable OG metatags  - Example: &OpenGraph=`1`\n| OGfbappId your facebook app id - Example: &OGfbappId=`123456789123456789`\n| OGtype - default: website - Example &OGtype=`article`\n| OGimageTv - default: thumbnail - Example: &OGimageTv=`my-thumbnail`\n|\n| ******* GooglePlus  Parameters: ******\n| GooglePlus - enable G+ metatags  - Example: &GooglePlus=`1`\n| linkPub your google publisher page  - Example: &linkPub=`https://plus.google.com/123456789123456789`\n|\n| Usage:\n|  Insert [[Seo4Evo]] anywhere in the head section of your template.\n|\n|  with custom MetaTags Tv (for old sites)\n|  [[Seo4Evo? &KeywordsTv=`documentTags`]]\n|\n|  With Facebook Open Graph metatags\n| [[Seo4Evo? &OpenGraph=`1` &OGfbappId=`123456789123456789` &OGimageTv=`my-thumbnail` &OGtype=`article`]]\n|\n| With Facebook Open Graph e Google plus metatags\n| [[Seo4Evo? &OpenGraph=`1` &OGfbappId=`123456789123456789` &OGimageTv=`my-thumbnail` &OGtype=`article` &GooglePlus=`1` &linkPub=`https://plus.google.com/123456789123456789`]]\n| ---------------------------------------------------------------------------\n\n*/\n$Keywords = isset($KeywordsTv) ? $KeywordsTv : \'[*MetaKeywords*]\';\n$MetaDescriptionTv = isset($MetaDescriptionTv) ? $MetaDescriptionTv : \'MetaDescription\';\n$MetaKeywords =\"\";\n$comma=(isset($all_page_keywords))?\', \':\'\';\n// *** KEYWORDS ***\n$MetaKeywords= \"<meta name=\\\"keywords\\\" content=\\\"{$all_page_keywords}{$comma}{$Keywords}\\\">\\n\";\n$MetaCharset =\"\";\n$BaseUrl =\"\";\n$MetaDesc = \"\";\n$MetaRobots = \"\";\n$MetaCopyright = \"\";\n\n$id = $modx -> documentObject[\'id\'];\n$url = $modx->makeUrl($id, \'\', \'\', \'full\');\n\n// *** Meta Title ***\n$preTitle = isset($preTitle) ? $preTitle : \'\';\n$postTitle = isset($postTitle) ? $postTitle : \'\';\n\n$pagetitle = $modx->documentObject[\'pagetitle\'];\n$CTitle = $modx->getTemplateVarOutput(\'CustomTitle\',$id);\n$Custom = $CTitle[\'CustomTitle\'];\n\nif(!$Custom == \"\"){\n$MetaTitle = \"<title>$preTitle$Custom$postTitle</title>\\n\";\n} else {\n      $MetaTitle = \"<title>$preTitle$pagetitle$postTitle</title>\\n\";\n   }\n\n// *** Meta Charset***\n$MetaCharset = \"<meta charset=\\\"[(modx_charset)]\\\">\\n\";\n// *** BASEURL***\n$BaseUrl = \"<base href=\\\"[(site_url)]\\\">\\n\";\n\n// *** Meta Description ***\n$dyndesc = $modx->runSnippet(\n        \"DynamicDescription\",\n        array(\n            \"descriptionTV\" => \"$MetaDescriptionTv\",\n			\"maxWordCount=\" => \"25\"\n        )\n);\n\n$MetaDesc = \"<meta name=\\\"description\\\" content=\\\"$dyndesc\\\">\\n\";\n\n// *** Meta Robots***\n$MetaRobots = \"<meta name=\\\"robots\\\" content=\\\"[*RobotsIndex*], [*RobotsFollow*]\\\">\\n\";\n\n//*** Meta Copiright***\n$MetaCopyright = \"<meta name=\\\"copyright\\\" content=\\\"[(site_name)]\\\">\\n\";\n\n// *** Last Modified ***\n$editedon = date(r,$modx->documentObject[\'editedon\']);\n$MetaEditedOn = \"<meta http-equiv=\\\"last-modified\\\" content=\\\"$editedon\\\">\\n\";\n\n// ** FACEBOOK OPEN GRAPH PROTOCOL\n\n$imageUrl = isset($OGimageTv) ? $OGimageTv : \'thumbnail\';\n$type = isset($OGtype) ? $OGtype : \'website\';\n\n$MetaProperty = \"<meta property=\\\"og:site_name\\\" content=\\\"[(site_name)]\\\">\\n\";\n$MetaPropertyType = \"<meta property=\\\"og:type\\\" content=\\\"$type\\\">\\n\";\n$MetaPropertyUrl = \"<meta property=\\\"og:url\\\" content=\\\"$url\\\">\\n\";\n$MetaPropertyImage = \"<meta property=\\\"og:image\\\" content=\\\"[(site_url)][*$imageUrl*]\\\">\\n\";\n\n$MetaPropertyFbApp = \"<meta property=\\\"fb:app_id\\\" content=\\\"$OGfbappId\\\">\\n\";\n\n//Google Plus\n\n$linkPub = isset($linkPub) ? $linkPub : \'\';\n$LinkPublisher = \"<link rel=\\\"publisher\\\" href=\\\"$linkPub\\\">\\n\";\n\n$GAuthor = $modx->getTemplateVarOutput(\'GoogleAuthor\',$id);\n$GoogleAthorship = $GAuthor[\'GoogleAuthor\'];\nif(!$GoogleAthorship == \"\"){\n$LinkAuthor = \"<link rel=\\\"author\\\" href=\\\"$GoogleAthorship\\\">\\n\";\n}\n\n//*** Canonical**//\n// Custom CanonicalUrl tv\n// with tv empty > link to siteurl for homepage and to full alias url for other pages.\n\n$CUrl = $modx->getTemplateVarOutput(\'CanonicalUrl\',$id);\n$CanonicalUrl = $CUrl[\'CanonicalUrl\'];\n\nif(!$CanonicalUrl == \"\"){\n$Canonical = \"<link rel=\\\"canonical\\\" href=\\\"$CanonicalUrl\\\">\\n\";\n} else {\n	$Canonical = $modx->documentIdentifier == $modx->config[\'site_start\'] ? \"<link rel=\\\"canonical\\\" href=\\\"[(site_url)]\\\" />\" : \"<link rel=\\\"canonical\\\" href=\\\"[(site_url)][~[*id*]~]\\\">\\n\";\n}\n\n// *** RETURN RESULTS ***\n// you can change the order of displayed items:\n$output = $MetaCharset.$BaseUrl.$MetaTitle.$MetaKeywords.$MetaDesc.$MetaRobots.$MetaCopyright.$MetaEditedOn.$Canonical;\n//return OpenGraph metatags if OpenGraph=1\nif ($OpenGraph >= 1) {\n    $output .= $MetaProperty.$MetaPropertyType.$MetaPropertyUrl.$MetaPropertyImage.$MetaPropertyFbApp;\n}\n//return Google plus metatags if GooglePlus=1\nif ($GooglePlus >= 1) {\n    $output .