                    $str = substr($str, 0, $pos).\'CacheAccelerator? &snippetToCache=`\'.$snippetToCache.\'`\'.$logMessages.\' &cacheId=`\'.md5(substr($str, $pos, $pos3 - $pos)).\'`\'.$dcfStr.substr($str, $pos1 + $offs);
